import scopes from '../apiServer/scopes';
import history from '../history';
import {
    SIGN_IN,
    SIGN_OUT,
    CREATE_STREAM,
    FETCH_STREAMS,
    FETCH_STREAM,
    DELETE_STREAM,
    EDIT_STREAM 
} from './types';

//Action Creator signIn
export const signIn = userId => {
    return {
        type: SIGN_IN,
        payload: userId
    }
}

//Action Creator signOut
export const signOut = () => {
    return {
        type: SIGN_OUT
    }
}

//Action Creator createStream
export const createStream = formValues => async (dispatch, getState) => {
    const { userId } = getState().auth;
    const response = await scopes.post('/scopes', {...formValues, userId });

    dispatch({type: CREATE_STREAM, payload: response.data})
    // Programmatic navigation to get the user back to root route
    history.push('/');
}

//Action Creator fetchStreams
export const fetchStreams = () => async dispatch => {
    const response = await scopes.get('/scopes');

    dispatch({type: FETCH_STREAMS, payload: response.data})
}

//Action Creator fetchStream
export const fetchStream = id => async dispatch => {
    const response = await scopes.get(`/scopes/${id}`);

    dispatch({type: FETCH_STREAM, payload: response.data})
}

//Action Creator editStream
export const editStream = (id, formValues) => async dispatch => {
    const response = await scopes.patch(`/scopes/${id}`, formValues);

    dispatch({type: EDIT_STREAM, payload: response.data})
    history.push('/');  //Navigate user back to homepage
}

//Action Creator deleteStream
export const deleteStream = id => async dispatch => {
    await scopes.delete(`/scopes/${id}`);

    dispatch({type: DELETE_STREAM, payload: id})
    history.push('/');  //Navigate user back to homepage 
}
